before.sql:
update exceptions_compared
set exc_prcnt_a = cast(exc_cnt_a as float)/cast(total as float)
where total > 0;

update exceptions_compared
set exc_prcnt_b = cast(exc_cnt_b as float)/cast(total as float)
where total > 0;

update exceptions_compared
set notes = 'YAY!'
where total > 100 and (exc_prcnt_a-exc_prcnt_b) > 0.10;

update exceptions_compared
set notes = 'YIKES!'
where total > 100 and (exc_prcnt_b-exc_prcnt_a) > 0.10;

insert into tcc_tmp (
    select pa.mime_id, pb.mime_id, sum(num_tokens) as cnt from profiles_a pa
    join profiles_b pb on pa.id=pb.id
    join contents_a c on c.id = pa.id
    group by pa.mime_id, pb.mime_id
      );

insert into tcc_tmp (
      select pa.mime_id, pb.mime_id, sum(num_tokens) as cnt from profiles_b pb
      join profiles_a pa on pa.id=pb.id
      join contents_b c on c.id = pb.id
      group by pa.mime_id, pb.mime_id
      );

insert into tcc_tmp (
