insert into ptc_tmp (
    select pa.mime_id, pb.mime_id, sum(pa.elapsed_time_millis) as total from profiles_a pa
    join profiles_b pb on pa.id=pb.id
    group by pa. mime_id, pb.mime_id
      );

insert into ptc_tmp (
      select pa.mime_id, pb.mime_id, sum(pb.elapsed_time_millis) as total from profiles_b pb
      join profiles_a pa on pb.id=pa.id
      group by pa.mime_id, pb.mime_id
      );

update parse_time_compared ptc set prcnt_increase=(100.0 *
cast(total_b as double)/cast(total_a as double))
where total_a > 0;
